import * as React from 'react';
import './counter.css'

export default function Counter({ counter, add, remove }) {
    return (
        <div className='count'>
            <button class="btn-floating grey" onClick={()=>remove()}><i class="material-icons">remove</i></button>
            <div className='number'>
                {counter}
            </div>
            <button className="btn-floating grey" onClick={()=>add()}><i class="material-icons">add</i></button>
        </div>
    )
}


// Si usamos export default, no es necesario importarlo como { componente } en App.js
