import React, { useState, useEffect } from 'react';
import Item from '../Components/Item/Item'

// Si uso el fetch y no hardcodeo los productos, no hace falta hacer la parte de setTimeOut porque no necesitamos emularlo

const ItemListContainer = ({ min, max }) => {
    const [products, setProducts] = useState([]);


    /* useEffect(() => {
        const timer = setTimeout(() => {
          console.log('This will run after 1 second!')
        }, 1000);
        return () => clearTimeout(timer);
      }, []);*/

    useEffect(() => {
        const delay = setTimeout(() => {
            const getProducts = fetch('https://api.mercadolibre.com/sites/MLA/search?category=MLA1743&limit=10'); // esto es ASYNC, por eso después va un "then"
            getProducts
                .then((res) => {
                    const data = res.json();
                    return data;
                })
                .then((data) => {
                    // console.log( "mis datos:", data.results);
                    setProducts(data.results);
                }
                )
        }, 2000);
        return () => clearTimeout(delay);
    }, []
    );
    useEffect(() => {
        console.log("productos", products);

    }, [products]);

    return (
        <div>
            {products.length && products.map((product) => (
                <Item title={product.title} id={product.id} price={product.price} img={product.thumbnail} />
            )
            )}
        </div>
    );
}

export default ItemListContainer;
