import React, { useState, useEffect } from 'react';
import Item from '../Components/Item/Item'


const ItemListContainer = ({ min, max }) => {
    const [products, setProducts] = useState([]);
    useEffect(() => {
        const delay = setTimeout(() => {
            // const getProducts = fetch('https://api.mercadolibre.com/sites/MLA/search?category=MLA1743&limit=10'); // esto es ASYNC, por eso después va un "then"
            const getProducts = fetch('https://api.tiendanube.com/v1/1115596/products/', { 
                method: 'get', 
                headers: new Headers({
                  'Authentication': 'bearer 9f2d915e98cb996410e12f6691c1b9f9c11a2d34', 
                  'Content-Type': 'application/json',
                  'Access-Control-Allow-Origin': '*'
                }), 
              });


            getProducts
                .then((res) => {
                    const data = res.json();
                    return data;
                })
                .then((data) => {
                    console.log( "mis datos:", data.results);
                    setProducts(data.results);
                }
                )
        }, 2000);
        return () => clearTimeout(delay);
    }, []
    );

    return (
        <div>
            {products.length && products.map((product) => (
                <Item name={product.name} id={product.id} price={product.price} img={product.picture} />

            )
            )}
        </div>
    );
}

export default ItemListContainer;
