import * as React from 'react';
import './counter.css'

export default function Count({counter, onAdd, sumar}) {
    return (
        <div className='count'>
            <Button variant="danger" className='mr-4' onClick={()=>onAdd('-')}> - </Button>
            <div>
                {counter}
            </div>
            <Button variant="success" className='ml-4' onClick={()=>onAdd('+')}> + </Button>
        </div>
    )
}
